mapper.update:
UPDATE progress
id = #{pojo.id}, account = #{pojo.account}, process_id = #{pojo.process_id}, data = #{pojo.data},
msg = #{pojo.msg}, files = #{pojo.files}, current_sequence = #{pojo.current_sequence}
WHERE id = #{pojo.id}

mapper.select:
SELECT 
FROM progress

     AND id = #{pojo.id} 
     AND account = #{pojo.account} 
     AND process_id = #{pojo.process_id} 
     AND data = #{pojo.data} 

     AND msg = #{pojo.msg} 
     AND files = #{pojo.files} 
     AND current_sequence = #{pojo.current_sequence} 

 LIMIT 1000
